import React from 'react';
import './styles.scss';
import { formatDate } from '../../utils/helpers';

const Education = () => {
  const education = [
    {
      school: 'STT Bandung',
      degree: 'S1 Teknik Informatika',
      startDate: '2022',
      endDate: 'Sekarang',
      description: 'Fokus pada pengembangan aplikasi web dan mobile. Mempelajari pemrograman backend dan frontend secara mendalam.'
    },
    {
      school: 'SMKN 7 Baleendah',
      degree: 'Rekayasa Perangkat Lunak',
      startDate: '2019',
      endDate: '2022',
      description: 'Jurusan RPL dengan fokus pada pemrograman web dan desktop. Mempelajari dasar-dasar algoritma dan struktur data.'
    }
  ];

  return (
    <section className="education" id="education">
      <div className="container">
        <h2 className="section-title">Education</h2>
        <div className="education__timeline">
          {education.map((edu, index) => (
            <div key={index} className="education__item">
              <h3>{edu.degree}</h3>
              <h4>{edu.school}</h4>
              <p className="date">{formatDate(edu.startDate)} - {formatDate(edu.endDate)}</p>
              <p>{edu.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Education;
