import React from 'react';
import './styles.scss';

const Hero = () => {
  const handleDownloadCV = () => {
    // Get the absolute path to the PDF file
    const pdfUrl = process.env.PUBLIC_URL + '/CV_rahman_2025.pdf';
    
    // Create an anchor element and trigger download
    const link = document.createElement('a');
    link.href = pdfUrl;
    link.download = 'CV_rahman_2025.pdf'; // Suggested filename for download
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <section className="hero" id="home">
      <div className="container hero__content">
        <div className="hero__profile">
          <img src="/images/profile/foto-profile.jpg" alt="Muhammad Ruhiyatna Rahman" />
        </div>
        <h1>Muhammad Ruhiyatna Rahman</h1>
        <h2>Software Developer | Network Engineer | SIG Analyst | AI-ML Engineer</h2>
        <p>Saya adalah seorang Developer yang selalu antusias untuk belajar teknologi baru dan mengembangkan solusi web yang inovatif.</p>
        <button className="cta-button" onClick={handleDownloadCV}>
          Unduh CV
        </button>
      </div>
    </section>
  );
};

export default Hero;
